import "./App.css";
import React, { useState } from "react";
import Form from "./components/Form";
import ProductList from "./components/ProductsList";

function App(props) {
  const [data, setData] = useState([
    { id: "1", name: "Horlics", price: 10 },
    { id: "2", name: "pepsi", price: 20 },
  ]);
  const OnAddCalled = (newData) => {
    setData((prevData) => {
      const updateData = [...prevData, newData];
      return updateData;
    });
  };
  const deleteItemHandler = (itemId) => {
    setData((prevData) => {
      const updatedData = prevData.filter((d) => d.id !== itemId);
      return updatedData;
    });
  };
  console.log(data);
  return (
    <React.Fragment>
      <Form onAdd={OnAddCalled}></Form>
      <ProductList items={data} onDeleteItem={deleteItemHandler}></ProductList>
    </React.Fragment>
  );
}

export default App;
