import React, { useState } from "react";
import Form from "./components/Form";
import ProductList from "./components/ProductsList";

function App() {
  const [data, setData] = useState([]);

  const OnAddCalled = (newData) => {
    setData((prevData) => {
      const updateData = [...prevData, newData];
      return updateData;
    });
  };
  const deleteItemHandler = (itemId) => {
    setData((prevData) => {
      const updatedData = prevData.filter((d) => d.id !== itemId);
      return updatedData;
    });
    localStorage.removeItem(itemId);
  };
  console.log(data);
  return (
    <React.Fragment>
      <Form onAdd={OnAddCalled}></Form>

      <ProductList items={data} onDelete={deleteItemHandler}></ProductList>
    </React.Fragment>
  );
}

export default App;
