import React, { useEffect, useState } from "react";
const ProductList = (props) => {
  let [total, setTotal] = useState(0);

  useEffect(() => {
    let addition = 0;

    for (let i = 0; i < props.items.length; i++) {
      addition = addition + Number(props.items[i].price);
    }
    setTotal(addition);
  }, [props.items]);
  const onDeleteItem = (id) => {
    props.onDelete(id);
  };
  return (
    <React.Fragment>
      <h1>Products</h1>
      <ul>
        {props.items.map((item) => {
          return (
            <li key={`${Math.random()}`}>
              {item.id}-{item.name}
              {item.price}
              <button id={item.id} onClick={onDeleteItem}>
                Delete Item
              </button>
            </li>
          );
        })}
      </ul>
      <h3>Total Worth Of the Products:{total}</h3>
    </React.Fragment>
  );
};
export default ProductList;
