import React, { useEffect, useState } from "react";
const ProductList = (props) => {
  let [total, setTotal] = useState(0);

  useEffect(() => {
    let addition = 0;
    for (let i in props.data) {
      addition = addition + i.price;
    }
    setTotal(addition);
  }, [props.data]);
  return (
    <React.Fragment>
      <h1>Products</h1>
      <ul>
        {props.data.map((d) => {
          <li key={d.id}>
            {d.id}-{d.name} {d.price}
            <button id={d.id}>Delete</button>
          </li>;
        })}
      </ul>
      <h3>Total Worth Of the Products:{total}</h3>
    </React.Fragment>
  );
};
export default ProductList;
