import React, { useEffect, useState } from "react";
const ProductList = (props) => {
  let [total, setTotal] = useState(0);

  useEffect(() => {
    let addition = 0;
    for (let i in props.items) {
      addition = addition + i.price;
    }
    setTotal(addition);
  }, [props.items]);
  console.log(props.data);
  return (
    <React.Fragment>
      <h1>Products</h1>
      <ul>{props.items}</ul>
      <h3>Total Worth Of the Products:{total}</h3>
    </React.Fragment>
  );
};
export default ProductList;
