import "./App.css";
import React, { useState } from "react";
import Form from "./components/Form";
import ProductList from "./components/ProductsList";

function App(props) {
  let data = {};
  const [seed, setSeed] = useState("1");
  const OnAddCalled = (newData) => {
    setSeed(Math.random());
    return (data = [...data, { newData }]);
  };
  console.log(data);
  return (
    <React.Fragment>
      <Form onAdd={OnAddCalled}></Form>
      <ProductList data={props.data}></ProductList>
    </React.Fragment>
  );
}

export default App;
