import "./App.css";
import React, { useState } from "react";
import Form from "./components/Form";
import ProductList from "./components/ProductsList";

function App(props) {
  const [data, setData] = useState({ key: "hii", name: "ashish" });
  const OnAddCalled = (newData) => {
    setData((prevData) => {
      const updateData = [...prevData, newData];
      return updateData;
    });
  };
  console.log(data);
  return (
    <React.Fragment>
      <Form onAdd={OnAddCalled}></Form>
      <ProductList items={data}></ProductList>
    </React.Fragment>
  );
}

export default App;
